import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './RightPanel.css';

const RightPanel = () => {
  const navigate = useNavigate();
  const [currentDate, setCurrentDate] = useState(new Date());
  const [calendarDate, setCalendarDate] = useState(new Date());

  useEffect(() => {
    const timer = setInterval(() => {
      setCurrentDate(new Date());
    }, 1000);
    return () => clearInterval(timer);
  }, []);

  const formatTime = (date) => {
    return date.toLocaleTimeString('en-US', {
      hour: '2-digit',
      minute: '2-digit',
      second: '2-digit',
      hour12: true
    });
  };

  const formatDate = (date) => {
    return date.toLocaleDateString('en-US', {
      weekday: 'long',
      year: 'numeric',
      month: 'long',
      day: 'numeric'
    });
  };

  const generateCalendar = () => {
    const year = calendarDate.getFullYear();
    const month = calendarDate.getMonth();
    const firstDay = new Date(year, month, 1);
    const lastDay = new Date(year, month + 1, 0);
    const firstDayOfWeek = firstDay.getDay();
    const daysInMonth = lastDay.getDate();
    
    const days = [];
    
    // Add empty cells for days before the first day of the month
    for (let i = 0; i < firstDayOfWeek; i++) {
      const prevMonthDay = new Date(year, month, -firstDayOfWeek + i + 1);
      days.push(
        <div key={`prev-${i}`} className="calendar-day other-month">
          {prevMonthDay.getDate()}
        </div>
      );
    }
    
    // Add days of the current month
    for (let day = 1; day <= daysInMonth; day++) {
      const isToday = 
        year === currentDate.getFullYear() &&
        month === currentDate.getMonth() &&
        day === currentDate.getDate();
      
      days.push(
        <div key={day} className={`calendar-day ${isToday ? 'today' : ''}`}>
          {day}
        </div>
      );
    }
    
    return days;
  };

  const navigateMonth = (direction) => {
    const newDate = new Date(calendarDate);
    newDate.setMonth(calendarDate.getMonth() + direction);
    setCalendarDate(newDate);
  };

  const getMonthYearString = () => {
    return calendarDate.toLocaleDateString('en-US', {
      month: 'long',
      year: 'numeric'
    });
  };

  return (
    <aside className="right-panel">
      {/* Teacher Profile Section */}
      <section className="profile-section">
        <div className="profile-card">
          <div className="account-badge">Teacher Account</div>
          <div className="profile-info">
            <div className="profile-avatar">
              <img 
                src="/teacher-profile.png" 
                alt="Teacher Profile" 
                className="profile-image"
                onError={(e) => {
                  e.target.style.display = 'none';
                  e.target.nextSibling.style.display = 'flex';
                }}
              />
              <div className="profile-icon" style={{display: 'none'}}>
                <i className="fas fa-user-tie"></i>
              </div>
            </div>
            <div className="profile-details">
              <h4 className="teacher-name">Richa Sharma</h4>
              <p className="teacher-id">ID: T012191</p>
            </div>
          </div>
        </div>
      </section>

      {/* Mini Calendar Section */}
      <section className="calendar-section">
        <div className="calendar-card">
          <div className="calendar-header">
            <h4>{getMonthYearString()}</h4>
            <div className="calendar-nav-buttons">
              <button 
                className="calendar-nav" 
                onClick={() => navigateMonth(-1)}
              >
                <i className="fas fa-chevron-left"></i>
              </button>
              <button 
                className="calendar-nav" 
                onClick={() => navigateMonth(1)}
              >
                <i className="fas fa-chevron-right"></i>
              </button>
            </div>
          </div>
          
          <div className="calendar-grid">
            <div className="calendar-weekdays">
              <div className="weekday">Sun</div>
              <div className="weekday">Mon</div>
              <div className="weekday">Tue</div>
              <div className="weekday">Wed</div>
              <div className="weekday">Thu</div>
              <div className="weekday">Fri</div>
              <div className="weekday">Sat</div>
            </div>
            
            <div className="calendar-days">
              {generateCalendar()}
            </div>
          </div>
        </div>
      </section>

      {/* Quick Actions Section */}
      <section className="quick-actions-section">
        <div className="quick-actions-card">
          <h4>Quick Actions</h4>
          <div className="quick-actions-grid">
            <button 
              className="quick-action-btn"
              onClick={() => navigate('/mark-attendance')}
            >
              <i className="fas fa-check-circle"></i>
              <span>Mark Attendance</span>
            </button>
            <button 
              className="quick-action-btn"
              onClick={() => navigate('/update-attendance')}
            >
              <i className="fas fa-edit"></i>
              <span>Update Records</span>
            </button>
            <button 
              className="quick-action-btn"
              onClick={() => navigate('/notices')}
            >
              <i className="fas fa-bell"></i>
              <span>Notices</span>
            </button>
            <button className="quick-action-btn">
              <i className="fas fa-download"></i>
              <span>Export Data</span>
            </button>
          </div>
        </div>
      </section>

      {/* Today's Summary Section */}
      <section className="summary-section">
        <div className="summary-card">
          <h4>Today's Summary</h4>
          <div className="summary-stats">
            <div className="summary-stat">
              <div className="stat-number">3</div>
              <div className="stat-label">Classes</div>
            </div>
            <div className="summary-stat">
              <div className="stat-number">96%</div>
              <div className="stat-label">Avg Attendance</div>
            </div>
            <div className="summary-stat">
              <div className="stat-number">76</div>
              <div className="stat-label">Total Students</div>
            </div>
            <div className="summary-stat">
              <div className="stat-number">6.5h</div>
              <div className="stat-label">Teaching Hours</div>
            </div>
          </div>
          <div className="next-class-info">
            <p><i className="fas fa-clock"></i> Next: Physics Lab at 2:00 PM</p>
          </div>
        </div>
      </section>
    </aside>
  );
};

export default RightPanel;
